# I'm using this script to test my heatwave definitions across all my sitesimport pandas as pd# Load climatological datahistorical_data = pd.read_csv('/Users/marleeyork/Documents/project2/data/extracted_daily_climate_data_wide_tmax.csv')historical_data.date = pd.to_datetime(historical_data.date)# Load in AmeriFlux dataAMF_data = loadAMF(path="/Users/marleeyork/Documents/project2/AMFdata")AMF_data.head()AMF_data.tail()# Initialize empty dictionaryheatwaves = {}# Loop through each site and define heatwavesfor site in AMF_data.Site.unique():    # Isolate site of interest in climatological and AMF data    historical_site_data = historical_data[['date',site]]    flux_data = AMF_data[AMF_data['Site']==site]        # Fit the heatwave defining function    site_heatwaves = fit_heatwaves(                      flux_dates = flux_data.TIMESTAMP_START,                       flux_temperature = flux_data.TA_F,                       historical_dates = historical_site_data['date'],                       historical_temperature = historical_site_data[site],                      quantile_threshold = .9,                      window_length = 15,                      threshold_comparison = 'greater',                      min_heatwave_length = 3,                      gap_days = 1,                      gap_days_window = 8,                      site = site                      )        # Add heatwaves into a dictionary with their site    heatwaves[site] = site_heatwaves    # For each site, plot the heatwavesfor site in AMF_data.Site.unique():    plt.show(heatwaves[site]["plot"])